const funcs = await pipedeps.pdUtils.manyFuncs(input.pipeFunctions)
function dependenciesFirst(a, b) {
    const aFunc = funcs.find(func => func.id === a);
    const bFunc = funcs.find(func => func.id === b);
    if (aFunc.dependency && !bFunc.dependency) return -1;
    if (!aFunc.dependency && bFunc.dependency) return 1;
    return 0;
}
input.updatedPipe.functions = input.updatedPipe.functions.toSorted(dependenciesFirst)
