async function hasReadPermissions(path) {
  try {
    // Attempting to read the file/directory
    await Deno.stat(path);
    return true;
  } catch (error) {
      return false;
  }
}


input.dirs = []
for await (const entry of Deno.readDir('/tmp')){
  const innerDir = '/tmp/'+entry.name
  if(await hasReadPermissions(innerDir) && entry.isDirectory) {
    try{
    for await (const innerEntry of Deno.readDir(innerDir)){
      if(innerEntry.isDirectory && innerEntry.name.includes('.git')) {
        input.dirs.push(entry);
        break;
       }
    }
    } catch (e) {}
  }
}
